import React, { Component } from 'react';
import { Button, Card, CardBody, CardFooter, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';
class Register extends Component {
  constructor(props){
    super(props);
    this.state = {
      Username:'',
      user_email:'',
      Password:'',
    }
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  	handleChange(event){
    const name  = event.target.name;
    const value = event.target.value;

    this.setState({
      [name]: value
    });
    } 

    handleSubmit=(event)=>{
     event.preventDefault();
     this.onFormSubmit(this.state);
      this.setState(this.initialState);

    }
    onFormSubmit(data){
      const apiUrl = "http://localhost/API/UserController/insertUsers";
      const myHeaders = new myHeaders();
      myHeaders.append('Content-Type','application/json');
      const options = {
        method:'POST',
        body:JSON.stringify(data),
        myHeaders
      };
      fetch(apiUrl,options)
        .then(res => res.json )
        .then(result =>{
          console.log(result);
        })
        .then(error=>console.log(error))

        this.setState({
        Username:'',
        user_email:'',
        Password:'',
        });
    }
  render() {
    return (
       <div className="app flex-row align-items-center">

                  <Form  onSubmit={this.handleSubmit}>

                      <Input   type="text" name="UserName" value={this.state.UserName}       onChange={this.handleChange} placeholder="Username" />

                      <Input   type="text" name="user_email" value={this.state.user_email}   onChange={this.handleChange}  placeholder="User email"  />

                      <Input   type="password" name="Password" value={this.state.Password}  onChange={this.handleChange} placeholder="Password"  />

                    <Button color="success">Create Account</Button>
                  </Form>

      </div>
    );
  }
}
export default Register;

