//     event.preventDefault();
//       fetch('http://localhost/API/UserController/insertUsers',{
      // method:'POST',
      // body:JSON.stringify({
      //   user_id :this.state.user_id,
      //   Username:this.state.Username,
      //   Password:this.state.Password,
      //   user_email:this.state.user_email,
      //   CreatedDate:this.state.CreatedDate,
      //   Status:this.state.Status,
      //   Role: this.state.Role,
      //   VendorId:this.state.VendorId,
      // }),
      // headers: {
      //         "Content-type": "application/json",
      // },
//       })
//       .then(res=> res.json())
//       .catch(error => console.log(error));
//       this.setState({
//             user_id:'',  Username:'', Password:'', user_email:'',CreatedDate:'',Status:'',Role:'',VendorId:'',
//       })
//       console.log("UserName:"+this.state.Username);
//     console.log("Password:"+this.state.Password);
//     console.log("Email:"+this.state.user_email);
//     }

handleSubmit =(event)=>{
  event.preventDefault();
  const usersInput = {
        user_id :this.state.user_id,
        Username:this.state.Username,
        Password:this.state.Password,
        user_email:this.state.user_email,
        CreatedDate:this.state.CreatedDate,
        Status:this.state.Status,
        Role: this.state.Role,
        VendorId:this.state.VendorId,
  };
  axios.post(`http://localhost/API/UserController/insertUsers`,{usersInput})
    .then(res => {
      console.log(res);
      console.log(res.data)
    })
}

  render() {


    return (
      <div className="app flex-row align-items-center">
        <Container>
          <Row className="justify-content-center">
            <Col md="9" lg="7" xl="6">
              <Card className="mx-4">
                <CardBody className="p-4">
                  <Form action="http://localhost/API/UserController/users" method="post" onSubmit={this.handleSubmit}>
                    <h1>Register</h1>
                    <p className="text-muted">Create your account</p>
                    <InputGroup className="mb-3">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="icon-user"></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input required  type="text" name="UserName"  onChange={this.handleChange} placeholder="Username" autoComplete="username" name="Username"  />
                    </InputGroup>
                    <InputGroup className="mb-3">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>@</InputGroupText>
                      </InputGroupAddon>
                      <Input required  type="text" placeholder="User email" name="user_email"  onChange={this.handleChange} autoComplete="email" />
                    </InputGroup>
                    <InputGroup className="mb-3">
                      <InputGroupAddon addonType="prepend">
                        <InputGroupText>
                          <i className="icon-lock"></i>
                        </InputGroupText>
                      </InputGroupAddon>
                      <Input required  type="password" placeholder="Password"  onChange={this.handleChange} name="Password" autoComplete="new-password" />
                    </InputGroup>
                    <Button color="success" block>Create Account</Button>
                  </Form>
                </CardBody>
                <CardFooter className="p-4">
                  <Row>
                    <Col xs="12" sm="6">
                      <Button className="btn-facebook mb-1" block><span>facebook</span></Button>
                    </Col>
                    <Col xs="12" sm="6">
                      <Button className="btn-twitter mb-1" block><span>twitter</span></Button>
                    </Col>
                  </Row>
                </CardFooter>
              </Card>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Register;
