import React, { Component } from 'react';
import axios from 'axios';
class Register extends Component {
  constructor(props){
    super(props);
    this.state = {
    Username:'', Password:'', user_email:'',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  	handleChange(event){
    this.setState({[event.target.name] : event.target.value});
    }  
handleSubmit=(event)=>{
     event.preventDefault();
     const user = {
      Username:this.state.Username,
        Password:this.state.Password,
        user_email:this.state.user_email,
     };
     axios.post(`http://localhost/API/UserController/insertUsers`,{user})
      .then(res => {
        console.log(res);
        console.log(res)
      });

    //  fetch('http://localhost/API/UserController/insertUsers', {
    //   method: 'POST',
    //   body:JSON.stringify({
    //     Username:this.state.Username,
    //     Password:this.state.Password,
    //     user_email:this.state.user_email,
    //   }) ,
    //   headers: {
    //            Accept: 'application/json',
    //           "Content-type": "application/json; charset=UTF-8"
    //   }
    // }).then(response => {
    //     if(response.status === 200) {
    //       alert("New website saved successfully");
    //     }
    //   });
    this.setState({
      user_id:'',  Username:'', Password:'', user_email:'',CreatedDate:'',Status:'',Role:'',VendorId:''
    })
    }
  render() {
    return (
      <div className="app flex-row align-items-center">

                  <form  action="" method="post" onSubmit={this.handleSubmit}>
                    <input required  type="text" name="UserName"  onChange={this.handleChange} placeholder="Username" autoComplete="username" name="Username"  />
                    <input required  type="text" placeholder="User email" name="user_email"  onChange={this.handleChange} autoComplete="email" />
                    <input required  type="password" placeholder="Password"  onChange={this.handleChange} name="Password" autoComplete="new-password" />
                    <button color="success" block>Create Account</button>
                  </form>
      </div>
    );
  }
}
export default Register;
